import React from 'react';
import PropTypes from 'prop-types';
import { platform, IOS } from '@vkontakte/vkui';
import Panel from '@vkontakte/vkui/dist/components/Panel/Panel';
import PanelHeader from '@vkontakte/vkui/dist/components/PanelHeader/PanelHeader';
import HeaderButton from '@vkontakte/vkui/dist/components/HeaderButton/HeaderButton';
import Icon28ChevronBack from '@vkontakte/icons/dist/28/chevron_back';
import Icon24Back from '@vkontakte/icons/dist/24/back';


import Snackbar from '@vkontakte/vkui/dist/components/Snackbar/Snackbar';
import CellButton from '@vkontakte/vkui/dist/components/CellButton/CellButton';
import Group from '@vkontakte/vkui/dist/components/Group/Group';
import Div from '@vkontakte/vkui/dist/components/Div/Div';
import Avatar from '@vkontakte/vkui/dist/components/Avatar/Avatar';
import View from '@vkontakte/vkui/dist/components/View/View';

import Icon16Done from '@vkontakte/icons/dist/16/done';

import io from 'socket.io-client';



const osName = platform();

const blueBackground = {
  backgroundColor: 'var(--accent)'
};
var sevents = '';

class Server extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			text: '',
			snackbar: null
		};
	
		this.openBase = this.openBase.bind(this);
	}

	componentDidMount() {
		this.openBase()
	}


	connect () {
		const axios = require('axios');
		const response =
			axios.get("https://admin-chat-bot.herokuapp.com/",
				{ headers: {'Content-Type': 'application/json'}}
			)
		this.setState({ text : response.data });
		console.log(response.data)
	} 

	openBase () {
		if (this.state.snackbar) return;
		this.setState({ snackbar:
		<Snackbar
			layout="vertical"
			onClose={() => this.setState({ text : 'v 1.35', snackbar: null })}
			before={<Avatar size={24} style={blueBackground}><Icon16Done fill="#fff" width={14} height={14} /></Avatar>}
		>
			This is snack bar
		</Snackbar>
		});
	}

	render() {
		const props = this.props;
		return (
			<Panel id={props.id}>
				<PanelHeader
					left={<HeaderButton onClick={props.go} data-to="home">
						{osName === IOS ? <Icon28ChevronBack/> : <Icon24Back/>}
					</HeaderButton>}
				>
					Server
				</PanelHeader>
			
				<Group>
				<CellButton onClick={this.openBase}>Snackbar</CellButton>
				<CellButton onClick={this.connect}>Try to connect</CellButton>
				</Group>
				
				{this.state.text && <Group><Div>{this.state.text}</Div><Div>{sevents}</Div></Group>}
	
				{this.state.snackbar}
			</Panel>
		);
	}
}

Server.propTypes = {
	id: PropTypes.string.isRequired,
	go: PropTypes.func.isRequired,
};

export default Server;
