import React, { PureComponent } from 'react';
import '@vkontakte/vkui/dist/vkui.css';
import '../css/App.css';
import {Separator} from "@vkontakte/vkui";
import Tappable from "@vkontakte/vkui/dist/components/Tappable/Tappable";

export class CardElements extends PureComponent {

    render() {
        let scheme;
        try{
            scheme  = document.body.attributes.getNamedItem('scheme').value;
        }catch (e) {
            scheme = 'client_light';
        }
        const light = { backgroundColor: '#ffffff' };
        const dark = { backgroundColor: '#363738' };
        const childrens = React.Children.map(this.props.children, (child, i) => {
            return
            <Tappable className={ `${i === 0 ? "tappable_top" : (i === (this.props.children.length-1) ? "tappable_bottom" : "tappable_center")}` }>
                <div>{child}{i !== (this.props.children.length-1) && ( scheme === 'space_gray' ? <Separator style={{ backgroundColor: '#626364' }} wide /> : <Separator wide /> )}</div>
            </Tappable>;
        });
        return (
            <div className={ "card-elements-container" } style={ scheme === 'space_gray' ? dark : light }>
                {childrens}
            </div>
        )
    }

}
