import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import './App.css'
import eruda from 'eruda';

import { Panel, PanelHeader, Div , Footer, View , ScreenSpinner , ModalPage , ModalCard , ModalRoot , ModalPageHeader
	, List , Cell , CellButton , InfoRow , Progress , Gallery , Counter , FormLayout , Input , Placeholder , Snackbar , Avatar , Group , Alert, Epic, Tabbar, TabbarItem, Card, CardGrid, Link
	, Tooltip , Tabs , TabsItem, platform, IOS, PanelHeaderSimple, PanelHeaderContent, PanelHeaderContext  } from '@vkontakte/vkui';
import Icon24Write from '@vkontakte/icons/dist/24/write';
import Icon16Dropdown from '@vkontakte/icons/dist/16/dropdown';

var user = null;

class App extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			activePanel: 'main',
			mainContextOpened: false,
			popout: <ScreenSpinner/>,

			user: {
				first_name: '',
				last_name: '',
				photo_100: ''
			},
			end: 0,
			cardsMap: [],
			cards: null,
			maxCards: 30,
			gold: 'https://i.ibb.co/kHKsYbz/gold.png',
			emerald: 'https://i.ibb.co/6bpJt6Y/emerald.png',
			iron: 'https://i.ibb.co/KLs7Pjb/iron.png',
			cobblestone: 'https://i.ibb.co/mvvLWTF/cobblestone.png'
		};

		this.componentDidMount = this.componentDidMount.bind(this);
		this.initializeApp = this.initializeApp.bind(this);
		this.setCards = this.setCards.bind(this);
		this.toggleMainContext = this.toggleMainContext.bind(this);
	}

	componentDidMount () {
		eruda.init({ tool: ['console'] });
		bridge.subscribe(({ detail: { type, data }}) => {
			console.log(type, data);
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = data.scheme ? data.scheme : 'client_light';
				document.body.attributes.setNamedItem(schemeAttribute);
			} else if (type === 'VKWebAppGetUserInfoResult'){
				this.setState({ popout: null, user: data });
				this.initializeApp();
			}
		});
		user = bridge.send('VKWebAppGetUserInfo');
	}

	initializeApp() {
		this.setCards();
		setInterval(()=>{
			if(this.state.activePanel === 'mine'){
				var end = this.state.end-1;
				this.setState({end});
				if(end<0){
					this.setCards();
				}
			}
		},1000);
	}

	randInt(min, max) {
		return Math.floor(Math.random() * Math.floor(max+1))+min;
	}

	setCards() {
		this.setState({ end: 5 });
		var cardsMap = [];
		for(var i = 0; i < this.state.maxCards; i++){
			cardsMap.push(i);
		}
		this.setState({ cardsMap});
		var cards = [];

		const { cobblestone, iron, emerald, gold } = this.state;
		for(var i = 1; i <= this.state.maxCards; i++){
			var ore1 = this.randInt(0,1);
			var resource = i%this.randInt(i, this.state.maxCards-i)===0 ? (ore1===0 ? gold : emerald) : (ore1===0 ? cobblestone : iron);
			var card = <div className={"card"} id={"card_"+(i-1)+'_'+resource} onClick={(e)=> {
				var cards = this.state.cards;
				var id = e.currentTarget.id.split('_');
				cards[id[1]] = <div className={"card"}/>;
				this.setState({cards: cards});
			}} style={{ width: 50, height: 50}}><div className={"card_content"}> <img src={resource} style={{height: "80%"}}/></div></div>;
			cards.push(card);
		}
		this.setState({ cards });
	}

	toggleMainContext () {
		this.setState({ mainContextOpened: !this.state.mainContextOpened });
	}

	render() {
		return (
			<View activePanel={this.state.activePanel} popout={this.state.popout}>
				<Panel id="main">
					<PanelHeaderSimple>
						<PanelHeaderContent
							aside={<Icon16Dropdown style={{ transform: `rotate(${this.state.mainContextOpened ? '180deg' : '0'})` }} />}
							before={<Avatar size={36} src={this.state.user.photo_100} />}
							onClick={this.toggleMainContext()}
						>
							{this.state.user.first_name + ' ' + this.state.last_name}
						</PanelHeaderContent>
					</PanelHeaderSimple>
					<PanelHeaderContext opened={this.state.mainContextOpened} onClose={this.toggleMainContext}>
						<List>
							<Cell
								before={<Icon24Write/>}
								onClick={this.toggleMainContext}
							>
								Изменить ник
							</Cell>
						</List>
					</PanelHeaderContext>
				</Panel>
				<Panel id="mine">
					<PanelHeader>Шахта</PanelHeader>
					<div style={{ display: "flex", alignItems: "center", justifyContent: "center" }}>
						<div className={"grid"}>
							{this.state.cards}
						</div>
					</div>
					<Div>
						<Footer>
							До перезагрузки: {this.state.end}
						</Footer>
					</Div>
				</Panel>
			</View>
		);
	}
}

export default App;
