import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import './css/App.css'

import { Panel, PanelHeader, Div , View , ScreenSpinner, List , Cell , Gallery , FormLayout , Input , Avatar , Group , Epic, Tabbar, TabbarItem, Separator, Button, PanelHeaderContext, Header, PanelHeaderSimple, PanelHeaderContent, Placeholder, Tabs,
	TabsItem, PullToRefresh, Snackbar, Textarea, PanelHeaderBack, PanelHeaderClose, CellButton, Alert  } from '@vkontakte/vkui';

import Icon16Spinner from '@vkontakte/icons/dist/16/spinner';
import Icon16Done from '@vkontakte/icons/dist/16/done';
import Icon16Cancel from '@vkontakte/icons/dist/16/cancel';
import Icon16Dropdown from '@vkontakte/icons/dist/16/dropdown';
import Icon16Down from '@vkontakte/icons/dist/16/down';
import Icon16Up from '@vkontakte/icons/dist/16/up';
import Icon28ReportOutline from '@vkontakte/icons/dist/28/report_outline';
import Icon28ShareOutline from '@vkontakte/icons/dist/28/share_outline';
import Icon28CancelOutline from '@vkontakte/icons/dist/28/cancel_outline';
import Icon28WriteOutline from '@vkontakte/icons/dist/28/write_outline';
import Icon28HistoryForwardOutline from '@vkontakte/icons/dist/28/history_forward_outline';
import Icon28MarketOutline from '@vkontakte/icons/dist/28/market_outline';
import Icon28UserOutline from '@vkontakte/icons/dist/28/user_outline';
import Icon56HistoryOutline from '@vkontakte/icons/dist/56/history_outline';

import {StickerImg} from "./components/StickerImg";
import {StickerButton} from "./components/StickerButton";
import {StickerDescription} from "./components/StickerDescription";
import {StickerGrid} from "./components/StickerGrid";
import {StickerUploadButton} from "./components/StickerUploadButton";
import {CardElements} from "./components/CardElements";
import {CardElement} from "./components/CardElement";

import request from "request";

let access_token = '', upload_url1 = '';

const blueBackground = {
	backgroundColor: 'var(--accent)'
}, redBackground = {
	backgroundColor: 'var(--dynamic_red)'
};

class App extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			initialized: false,
			popout: <ScreenSpinner/>,
			activeStory: 'tab_main',
			slideTabIndex: 0,
			activePanelShop: "main",
			historyShop: ["main"],
			activePanelOther: "profile",
			historyOther: ["profile"],
			createStickerContextOpened: false,
			balance_manipulation: false,
			report_other_visible: false,
			stickers_panel: null,
			sticker_info: { title: "", author: "", preview: "", price: 0, description: "", stickers: [], sales: 0 },
			user: {
				first_name: '',
				last_name: '',
				photo_100: '',
				stickers:[ ],
				balance: 0,
				stickers_history: [ ]
			},
			create_panel: { title: "", preview: "", price: 1, description: "", stickers: [] },
			captcha : { text: '', url: '', done: false }
		};

		this.onStoryChange = this.onStoryChange.bind(this);
		this.onChange = this.onChange.bind(this);
		this.uploadFile = this.uploadFile.bind(this);
		this.toggleCreateStickerContext = this.toggleCreateStickerContext.bind(this);
		this.sync = this.sync.bind(this);
		this.install = this.install.bind(this);
		this.delete = this.delete.bind(this);
		this.create_stickers = this.create_stickers.bind(this);
		this.go_to_edit = this.go_to_edit.bind(this);

		this.componentDidMount = this.componentDidMount.bind(this);
		this.initializeApp = this.initializeApp.bind(this);
		this.updateStickers = this.updateStickers.bind(this);
		this.updateStickersInfo = this.updateStickersInfo.bind(this);
		this.updateUser = this.updateUser.bind(this);
		this.updateQuestions = this.updateQuestions.bind(this);
		this.back = this.back.bind(this);
		this.go = this.go.bind(this);
		this.change_hash = this.change_hash.bind(this);
	}

	componentDidMount () {
		window.addEventListener('popstate', e => {
			e.preventDefault();
			let activeStory = this.state.activeStory;
			let activePanel = activeStory === 'tab_main' ? this.state.activePanelShop : activeStory === 'tab_other' ? this.state.activePanelOther : '';
			if(activePanel !== 'input_captcha') {
				this.back(e);
			}
		});

		bridge.subscribe(async ({ detail: { type, data }}) => {
			if(type !== undefined) console.log(type, data);
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = data.scheme ? data.scheme : 'client_light';
				document.body.attributes.setNamedItem(schemeAttribute);
			} else if (type === 'VKWebAppGetUserInfoResult'){
				if(!this.state.initialized){
					let login = await this.get('users.auth');
					if(login.access){
						/*
                        data.stickers = [ 0 ];
                        data.balance = 0;
                        data.stickers_history = [ { title: "Илай", preview: "https://vk.com/sticker/1-18781-128", added: false }, { title: "Бисквит", author: "Илья Ульянов", preview: "https://vk.com/sticker/2-533-cover-140b-0" } ];
                        */
						this.setState({ initialized: true, popout: null });
						this.initializeApp();
					}
				}
			} else if (type === 'VKWebAppViewRestore'){
				if(this.state.popout !== null){
					this.setState({ popout: null });
				}
			} else if (type === 'VKWebAppCallAPIMethodFailed'){
				if(data.error_data.error_reason.error_code === 14){
					let c = this.state.captcha;
					c.done = null;
					c.url = data.error_data.error_reason.captcha_img;
					c.sid = data.error_data.error_reason.captcha_sid;
					this.setState({ captcha: c, popout: null });
					this.go('input_captcha');
					for(var i = 0; i < 2; i++){
					    if(this.state.captcha.done === null){
					        i--;
                            await this.sleep(1000);
                        }else{
					        break;
                        }
                    }

					this.setState({ popout: <ScreenSpinner/> });
					let request_params = data.error_data.error_reason.request_params;
					let params = {};
					let method = '';
					let request_id = '';
					for(var i = 0; i < request_params.length; i++){
						let key = request_params[i].key;
						let value = request_params[i].value;
						if(key !== 'api_id' && key !== 'format' && key !== 'oauth'){
							if(key === 'method'){
								method = value;
							}else if (key === 'request_id'){
								request_id = value;
							}else{
								params[key] = value;
							}
						}
					}
					params.access_token = access_token;
					params.captcha_sid = this.state.captcha.sid;
					params.captcha_key = this.state.captcha.text;
					try{
						let res_back = await bridge.sendPromise("VKWebAppCallAPIMethod", {
							"method": method,
							"request_id": request_id,
							"params": params
						});
						this.setState({ captcha : { text: '', url: '', done: false } });
						this.back();
					}catch (e) {

					}
				}
			}
		});
		this.initializeApp();
	}

