	async get(method, params) {
		if(params === undefined || params === null) params = {};
		params.url = window.location.search.replace('?', '');
		let str = [];
		for (let p in params)
			if (params.hasOwnProperty(p)) {
				str.push(encodeURIComponent(p) + "=" + encodeURIComponent(params[p]));
			}
		params = str.join("&");
		let response = await fetch('https://ih1705413.vds.myihor.ru:4040/'+method+'?'+params, {method: 'GET'})
			.then(res => res.json())
			.then(res => {
				return res;
			});
		console.log(response);
		return response;
	}

	sleep(ms) {
		return new Promise(resolve => setTimeout(resolve, ms));
	}
	urlToBase64(url){
		return new Promise(resolve => {
			let img = new Image();
			img.crossOrigin = 'Anonymous';
			img.onload = function(){
				let canvas = document.createElement('CANVAS'),
					ctx = canvas.getContext('2d'), dataURL;
				canvas.height = img.height;
				canvas.width = img.width;
				ctx.drawImage(img, 0, 0);
				dataURL = canvas.toDataURL("image/png");
				canvas = null;
				resolve(dataURL);
			};
			img.src = url;
		});
	}
	base64ToMultipart(base64) {
		return new Promise(resolve => {
			fetch(base64)
				.then(res => res.blob())
				.then(blob => {
					const fd = new FormData();
					const file = new File([blob], "sticker.png");
					fd.append('file', file);
					resolve(fd);
				});
		});
	}

	back = () => {
		let activeStory = this.state.activeStory;
		let activePanel = activeStory === 'tab_main' ? this.state.activePanelShop : activeStory === 'tab_other' ? this.state.activePanelOther : '';
		let panel_to_change = activeStory === 'tab_main' ? 'activePanelShop' : activeStory === 'tab_other' ? 'activePanelOther' : '';
		if((activeStory === 'tab_main' && activePanel==='main') || (activeStory === 'tab_other' && activePanel==='profile')){
			bridge.send("VKWebAppClose", {"status": "success"});
		}else {
			let history = activeStory === 'tab_main' ? this.state.historyShop : activeStory === 'tab_other' ? this.state.historyOther : '';
			if(history.length === 1) {
				bridge.send("VKWebAppClose", {"status": "success"});
			} else if (history.length > 1) {
				history.pop();
				let p = history[history.length - 1];
				this.setState({[panel_to_change]: p});
				this.change_hash(p);
			}
		}

	};
	go(panel) {
		let activeStory = this.state.activeStory;
		let panel_to_change = activeStory === 'tab_main' ? 'activePanelShop' : activeStory === 'tab_other' ? 'activePanelOther' : '';
		let history = activeStory === 'tab_main' ? this.state.historyShop : activeStory === 'tab_other' ? this.state.historyOther : '';
		if(panel.startsWith('sticker_info_')){
			let stickers_id = panel.split('_')[2];
			if(this.state.stickers.hasOwnProperty(stickers_id)){
				this.setState({ sticker_info: this.state.stickers[stickers_id] });
				panel = 'sticker_info';
			}else{
				panel = 'main';
			}
		}else if (panel === 'stickers_report'){
			this.setState({ report_other_visible: false });
		}
		if(history[history.length-1] !== panel) {
			history.push(panel);
			let history_to_change = activeStory === 'tab_main' ? 'historyShop' : activeStory === 'tab_other' ? 'historyOther' : '';
			window.history.pushState({panel: panel}, 'Title');
			this.setState({[panel_to_change]: panel, [history_to_change]: history});
		}
		this.change_hash(panel);
	}
	change_hash (panel){
		let activeStory = this.state.activeStory;
		if(panel === 'sticker_info'){
			panel = panel+'_'+this.state.sticker_info.id;
		}
		bridge.send("VKWebAppSetLocation", {"location": activeStory+'_'+panel});
	}

	onStoryChange (e) {
		let story = e.currentTarget.dataset.story;
		if(this.state.activeStory===story){
			if(story==='tab_main' && this.state.activePanelShop !== 'main'){
				this.back();
			}else if(story==='tab_other' && this.state.activePanelOther !== 'profile'){
				this.back();
			}
		}
		this.setState({ activeStory: story });
	}
