				image: upload_preview_img.replace('data:image/png;base64,','')
			}
		}, (error, response, body)=>{
			if(response.statusCode === 200){
				body = JSON.parse(body);
				let url = body.data.url;
				console.log(url);
				preview = url;
				uploaded++;
			}else{
				console.log('error', body);
			}
		});

		for(let sticker of uploaded_stickers_img){
			await request({
				url: 'https://murmuring-bastion-20764.herokuapp.com/https://api.imgbb.com/1/upload?key=b2bfb837d63c6adae4d191841abb04dc',
				method: 'POST',
