				}
			}, (error, response, body)=>{
				if(response.statusCode === 200){
					body = JSON.parse(body);
					let url = body.data.url;
					console.log(url);
					stickers.push(url);
					uploaded++;
				}else{
					console.log('error', body);
				}
			});
			await this.sleep(500);
		}

		for(let i = 0; i < 2; i++ ){
			if(uploaded === (uploaded_stickers_img.length+1)){
				break;
			}else{
				i--;
				await this.sleep(500);
			}
		}

		let resp = await this.get('stickers.create', { title, description, price, preview, stickers, stickers_id: this.state.create_panel.hasOwnProperty('stickers_id') ? parseInt(this.state.create_panel.stickers_id) : null });
		if(resp.status === 'ok'){
			this.setState({ create_panel: { title: "", preview: "", price: 1, description: "", stickers: [] } });
			await this.updateUser();
			this.go('stickers_history');
		}
		this.setState({ popout: null });
	}
	go_to_edit () {
		let create_panel = this.state.create_panel;
		let stickers_ = this.state.sticker_info.stickers;
		let stickers = [];
		create_panel.stickers_id = this.state.sticker_info.id;
		create_panel.title = this.state.sticker_info.title;
		create_panel.description = this.state.sticker_info.description;
		create_panel.price = this.state.sticker_info.price;
		create_panel.upload_preview_img = this.state.sticker_info.preview;
		for (let i = 0; i < stickers_.length; i++) {
			let sticker = stickers_[i];
			stickers.push(
				<StickerImg
					mode={'small'}
					id={'uploaded_sticker_' + i}
					src={sticker.props.src}
					onClick={(e) => {
						let id = parseInt(e.currentTarget.id.split('_')[2]);
						let create_panel_ = this.state.create_panel;
						delete create_panel_.uploaded_stickers_img[id];
						this.setState({create_panel: create_panel_});
					}}
				/>
			);
		}
		create_panel.uploaded_stickers_img = stickers;
		this.setState({ create_panel: create_panel, activeStory: 'tab_other' });
		setTimeout(()=>{
			this.go('create');
		},250);
	}

	render() {
		return (
			<Epic activeStory={this.state.activeStory} tabbar={
				<Tabbar>
					<TabbarItem
						onClick={this.onStoryChange}
						selected={this.state.activeStory === 'tab_main'}
						data-story="tab_main"
					><Icon28MarketOutline/></TabbarItem>
					<TabbarItem
						onClick={this.onStoryChange}
						selected={this.state.activeStory === 'tab_other'}
						data-story="tab_other"
					><Icon28UserOutline /></TabbarItem>
				</Tabbar>
			}>
				<View id="tab_main" activePanel={this.state.activePanelShop} popout={this.state.popout} history={this.state.historyShop} onSwipeBack={this.back} header={false}>
					<Panel id="main" separator={false}>
						<PanelHeaderSimple
							separator={false}
						>
							Стикеры
						</PanelHeaderSimple>
						<Tabs>
							<TabsItem
								onClick={() => this.setState({ slideTabIndex: 0 })}
								selected={this.state.slideTabIndex === 0}
							>
								Новинки
							</TabsItem>
							<TabsItem
								onClick={() => this.setState({ slideTabIndex: 1 })}
								selected={this.state.slideTabIndex === 1}
							>
								Популярные
							</TabsItem>
						</Tabs>
						<Separator />
						<PullToRefresh onRefresh={async () => {
							this.setState({ refreshedStickers: true });
							await this.updateStickers();
							await this.updateQuestions();
							this.setState({ refreshedStickers: false });
						}} isFetching={this.state.refreshedStickers}>
							<Gallery
								slideWidth="100%"
								style={{ height: "100%", margin: "8px" }}
								slideIndex={this.state.slideTabIndex}
								onChange={(slideIndex) => { this.setState({slideTabIndex: slideIndex}); }}>
								<div>
									<List style={{ marginTop: "15px" }}>
										{this.state.stickers_panel_new}
									</List>
								</div>

								<div>
									<List style={{ marginTop: "15px" }}>
										{this.state.stickers_panel_popular}
									</List>
								</div>
							</Gallery>
						</PullToRefresh>
					</Panel>
					<Panel id="sticker_info">
						<PanelHeaderSimple
							left={<PanelHeaderBack onClick={()=>{ this.back(); }} />}
						><PanelHeaderContent>
							Информация
						</PanelHeaderContent></PanelHeaderSimple>
						<PullToRefresh onRefresh={async () => {
							this.setState({ refreshedStickersInfo: true });
							await this.updateStickersInfo();
							await this.updateQuestions();
							this.setState({ refreshedStickersInfo: false });
						}} isFetching={this.state.refreshedStickersInfo}>
							<Div>
								<StickerButton
									title={this.state.sticker_info.title}
									description={this.state.sticker_info.author+'\nКуплено: '+(this.state.sticker_info.sales === 0 ? 0 : this.state.sticker_info.sales.length)}
									src={this.state.sticker_info.preview}
									mode={"cell"}
									more={
										this.state.sticker_info.author_id + '' === this.state.user.id + '' ?
											<Icon28WriteOutline onClick={()=>{ this.go_to_edit(); } } fill={'#4bb34b'}/> :
											<Icon28ReportOutline onClick={()=>{ this.go('stickers_report'); } } fill={'#eb4250'}/>
									}
								/>
								<div style={{ marginLeft: '8px', marginRight: '8px' }}>
									<StickerDescription>{this.state.sticker_info.description}</StickerDescription>
									<div style={{ marginTop: '10px'}}>
										{this.state.user.stickers.indexOf(this.state.sticker_info.id)>=0 ?
											<div style={{ display: 'flex' }}>
												<Button onClick={()=>{ this.install(this.state.sticker_info.id); }} size={"xl"} stretched>{'Установить'}</Button>
												{this.state.user.hasOwnProperty('stickers_cache') ? this.state.user.stickers_cache.hasOwnProperty(this.state.sticker_info.id) ?
													<Button style={{ marginLeft: '8px' }} mode={"secondary"} onClick={()=>{ this.delete(this.state.sticker_info.id); }} size={"xl"} stretched>{'Очистить'}</Button> : null : null}
											</div>
											:
											<div style={{ display: 'flex' }}>
												<Button size={"xl"} stretched mode="commerce">{'Получить за '+this.state.sticker_info.price+' руб.'}</Button>
											</div>}
										{/*this.state.user.is_admin*/ false && <div style={{ display: 'flex', marginTop: '10px' }}><Button onClick={()=> { this.sync(this.state.sticker_info.id); }} size={"xl"} stretched>{'Синхронизировать'}</Button></div>}
									</div>
								</div>
								<div className="centered" style={{ marginTop: "18px" }}>
									<StickerGrid mode={"small"}>
										{this.state.sticker_info.stickers}
									</StickerGrid>
								</div>
							</Div>

							{
								this.state.questions_panel &&
								<Div>
									<div className="centered">
										<h4>Ответы на вопросы</h4>
									</div>
									<div className="centered">
										<CardElements>
											{this.state.questions_panel}
										</CardElements>
									</div>
								</Div>
							}
							{this.state.snackbar}
						</PullToRefresh>
					</Panel>
					<Panel id="input_captcha">
						<PanelHeaderSimple><PanelHeaderContent>
							Ввод капчи
						</PanelHeaderContent></PanelHeaderSimple>
						<FormLayout>
							<div className="centered">
								<img width={'60%'} src={this.state.captcha.url}/>
							</div>
							<Input
								align={'center'}
								value={this.state.captcha.text}
								onChange={(e)=> { let c = this.state.captcha; c.text = e.currentTarget.value; this.setState({ captcha: c }); }}
							/>
							<Button size="xl" onClick={()=> { let c = this.state.captcha; c.done = true; this.setState({ captcha: c });  }}>Отправить</Button>
						</FormLayout>
					</Panel>
					<Panel id="stickers_report">
						<PanelHeaderSimple left={<PanelHeaderClose onClick={()=>{ this.back(); }} />}><PanelHeaderContent>
							Жалоба
						</PanelHeaderContent></PanelHeaderSimple>
						<Group header={<Header mode="secondary">Причина жалобы</Header>}>
							<CellButton>Оскорбление</CellButton>
							<CellButton>Материал для взрослых</CellButton>
							<CellButton>Пропаганда наркотиков</CellButton>
							<CellButton onClick={()=>{ this.setState({ report_other_visible: !this.state.report_other_visible }); }}>Другое</CellButton>
							{
								this.state.report_other_visible &&
									<FormLayout>
										<Textarea top="Опишите подробнее" />
										<Button size="xl">Отправить</Button>
									</FormLayout>
							}
						</Group>
					</Panel>
				</View>
				<View id="tab_other" activePanel={this.state.activePanelOther} popout={this.state.popout} history={this.state.historyOther} onSwipeBack={this.back} header={false}>
					<Panel id="profile">
						<PanelHeaderSimple><PanelHeaderContent>
							Профиль
						</PanelHeaderContent></PanelHeaderSimple>
						<PullToRefresh onRefresh={async () => {
							this.setState({ refreshedProfile: true });
							await this.updateUser();
							this.setState({ refreshedProfile: false });
						}} isFetching={this.state.refreshedProfile}>
							<Div>
								<div className="centered">
									<Avatar src={this.state.user.photo_100} size={100}/>
								</div>
								<br/>
								<div className="centered" style={{ fontSize: "large"}}>
									{this.state.user.first_name+' '+this.state.user.last_name}
								</div>
								<br/>
								<Group separator="hide">
									<Cell asideContent={<Icon16Dropdown style={{ transform: `rotate(${this.state.balance_manipulation ? '180deg' : '0'})` }} />} onClick={()=> { this.setState({ balance_manipulation: !this.state.balance_manipulation }); }} indicator={this.state.user.balance+' ₽'}>Ваш баланс</Cell>
									{this.state.balance_manipulation && <div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '8px' }}>
										<Button size="l" before={<Icon16Down/>}>Пополнить</Button>
										<Button size="l" before={<Icon16Up/>} mode="secondary" style={{ marginLeft: 8 }}>Вывести</Button>
									</div>}
								</Group>
								<Group separator="hide" header={<Header mode="primary">Ваши стикеры</Header>}>
									<div className="centered" style={{ marginTop: "18px" }}>
										<StickerGrid mode={"big"}>
											{this.state.user_stickers}
											<StickerImg mode={'big'} onClick={()=> { this.go("create"); }}>
												+
											</StickerImg>
										</StickerGrid>
									</div>
								</Group>
							</Div>
						</PullToRefresh>
					</Panel>
					<Panel id="create">
						<PanelHeaderSimple
							left={<PanelHeaderBack onClick={()=>{ this.back(); }} />}
						>
							<PanelHeaderContent
								aside={<Icon16Dropdown style={{ transform: `rotate(${this.state.createStickerContextOpened ? '180deg' : '0'})` }} />}
								onClick={ () => { this.toggleCreateStickerContext(); }}
							>
								Добавить стикеры
							</PanelHeaderContent>
						</PanelHeaderSimple>
						<PanelHeaderContext opened={this.state.createStickerContextOpened} onClose={ () => { this.toggleCreateStickerContext(); }}>
							<List>
								<Cell
									before={<Icon28HistoryForwardOutline/>}
									onClick={ () => { this.toggleCreateStickerContext(); this.go("stickers_history") }}
								>
									История
								</Cell>
							</List>
						</PanelHeaderContext>
						<FormLayout>
							<Input
								type="text"
								top="Название стикер-пака"
								name="title"
								value={this.state.create_panel.title}
								onChange={this.onChange}
								status={this.state.create_panel.title ? this.state.create_panel.title.length <= 20 ? 'valid' : 'error' : 'error'}
								bottom={this.state.create_panel.title ? this.state.create_panel.title.length <= 20 ? '' : 'Максимальная длина - 20' : 'Пожалуйста, введите название'}
							/>
							<Textarea
								type="text"
								top="Описание"
								name="description"
								value={this.state.create_panel.description}
								onChange={this.onChange}
								status={this.state.create_panel.description ? this.state.create_panel.description.length <= 100 ? 'valid' : 'error' : 'error'}
								bottom={this.state.create_panel.description ? this.state.create_panel.description.length <= 100 ? '' : 'Максимальная длина - 100' : 'Пожалуйста, введите описание'}
							/>
							<Input
								type="number"
								top="Цена"
								name="price"
								value={this.state.create_panel.price}
								onChange={this.onChange}
								status={this.state.create_panel.price > 0 ? this.state.create_panel.price <= 1000 ? this.state.create_panel.price.toString().includes('.') ? 'error' : 'valid' : 'error' : 'error'}
								bottom={this.state.create_panel.price > 0 ? this.state.create_panel.price <= 1000 ? this.state.create_panel.price.toString().includes('.') ? 'Число должно быть целым' : '' : 'Максимальная цена- 1 000 руб.' : 'Минимальная цена - 1 руб.'}
							/>

							<StickerUploadButton
								src={this.state.create_panel.upload_preview_img && this.state.create_panel.upload_preview_img}
								title={'Превью'}
								description={'Загрузите главную фотографию стикер-пака (128×128px)'}
								onChange={(event)=> {this.uploadFile('upload_preview_img', event)}}
							/>

							<StickerUploadButton
								title={'Стикеры'}
								description={'Загрузите все стикеры (минимум - 4, максимум 48), которые будут содержаться в паке (128×128px)'}
								onChange={(event)=> {this.uploadFile('upload_sticker_img', event)}}
							/>

							<div className="centered" style={{ paddingBottom: "16px" }}>
								<div style={{ minWidth: "64px", maxWidth: "296px", display: "grid", gridTemplateColumns: "repeat(auto-fill, 64px)", gridGap: "10px" }}>
									{this.state.create_panel.uploaded_stickers_img}
								</div>
							</div>

							<Button size="xl" onClick={()=>{ this.create_stickers(); }} disabled={this.state.create_panel.title ? this.state.create_panel.description ? this.state.create_panel.price > 0 ? this.state.create_panel.title.length <= 20 ? this.state.create_panel.description.length <= 100 ? this.state.create_panel.price <= 1000 ? this.state.create_panel.price.toString().includes('.') ?  true : this.state.create_panel.upload_preview_img ? this.state.create_panel.uploaded_stickers_img ? this.state.create_panel.uploaded_stickers_img.filter(function(val){ return val !== undefined; }).length >= 4 ? false : true : true : true : true : true : true : true : true : true }>Продолжить</Button>
						</FormLayout>
					</Panel>
					<Panel id={"stickers_history"}>
						<PanelHeaderSimple
							left={<PanelHeaderBack onClick={()=>{ this.back(); }} />}
						><PanelHeaderContent>
							История
						</PanelHeaderContent></PanelHeaderSimple>
						<PullToRefresh onRefresh={async () => {
							this.setState({ refreshedStickersHistory: true });
							await this.updateUser();
							this.setState({ refreshedStickersHistory: false });
						}} isFetching={this.state.refreshedStickersHistory}>
							{this.state.user_stickers_history && this.state.user_stickers_history.length > 0 ? <Div>
								<List>
									{this.state.user_stickers_history}
								</List>
							</Div>
							:
								<Placeholder
									icon={<Icon56HistoryOutline/>}
									header={'Пусто'}
									stretched
								>
									Вы пока еще не создавали стикеры
								</Placeholder>}
						</PullToRefresh>
					</Panel>
				</View>
			</Epic>
		);
	}
}

export default App;
