import React, { PureComponent } from 'react';
import '@vkontakte/vkui/dist/vkui.css';
import {Avatar} from "@vkontakte/vkui";
import Tappable from "@vkontakte/vkui/dist/components/Tappable/Tappable";

export class StickerImg extends PureComponent {

    render() {
        return (
            this.props.mode === 'small' ?
                <img width={64} height={64} src={this.props.src} id={this.props.id} onClick={this.props.onClick}/>
                :
                this.props.mode === 'medium' ?
                    <Avatar mode="app" size={78} src={this.props.src} />
                    :
                    this.props.mode === 'big' &&
                        <Tappable style={{ borderRadius: '50%' }}>
                            <Avatar size={96}>
                                {this.props.hasOwnProperty('children') ?
                                    <div className="centered" style={{ borderRadius: 48, width: "100%", height: "100%", color: "#7f8285", fontSize: 76 }} onClick={this.props.onClick}>{this.props.children}</div>
                                    :
                                    <img width={78} height={78}
                                         src={this.props.src}
                                         id={this.props.id}
                                         onClick={this.props.onClick}
                                    />
                                }
                            </Avatar>
                        </Tappable>
        )
    }

}
