import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import './App.css'

import { PopoutWrapper, ConfigProvider, Panel, PanelHeader, Div , Button , Footer, View , ScreenSpinner , ModalPage , ModalCard , ModalRoot , PanelHeaderButton , ModalPageHeader
	, List , Cell , CellButton , InfoRow , Progress , Gallery , Counter , FormLayout , Input , Placeholder , Snackbar , Avatar , Group , Alert, Epic, Tabbar, TabbarItem, Card, CardGrid, Link
	, Tooltip , Tabs , TabsItem, platform, IOS  } from '@vkontakte/vkui';

var user = null;

class App extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			activePanel: 'main',
			popout: <ScreenSpinner/>,

			end: 0,
			cardsMap: [],
			cards: null,
			maxCards: 30,
			gold: 'https://i.ibb.co/kHKsYbz/gold.png',
			emerald: 'https://i.ibb.co/6bpJt6Y/emerald.png',
			iron: 'https://i.ibb.co/KLs7Pjb/iron.png',
			cobblestone: 'https://i.ibb.co/mvvLWTF/cobblestone.png'
		};

		this.componentDidMount = this.componentDidMount.bind(this);
		this.initializeApp = this.initializeApp.bind(this);
		this.setCards = this.setCards.bind(this);
	}

	componentDidMount () {
		bridge.subscribe(({ detail: { type, data }}) => {
			console.log(detail);
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = data.scheme ? data.scheme : 'client_light';
				document.body.attributes.setNamedItem(schemeAttribute);
			} else if (type === 'VKWebAppGetUserInfoResult'){
				this.setState({ popout: null });
				this.initializeApp();
			}
		});
		user = bridge.send('VKWebAppGetUserInfo');
	}

	initializeApp() {
		this.setCards();
		setInterval(()=>{
			var end = this.state.end-1;
			this.setState({end});
			if(end<0){
				this.setCards();
			}
		},1000);
	}

	randInt(min, max) {
		return Math.floor(Math.random() * Math.floor(max+1))+min;
	}

	setCards() {
		this.setState({ end: 5 });
		var cardsMap = [];
		for(var i = 0; i < this.state.maxCards; i++){
			cardsMap.push(i);
		}
		this.setState({ cardsMap});
		var cards = [];

		const { cobblestone, iron, emerald, gold } = this.state;
		for(var i = 1; i <= this.state.maxCards; i++){
			var ore1 = this.randInt(0,1);
			var resource = i%this.randInt(i, this.state.maxCards-5-i)===0 ? (ore1===0 ? gold : emerald) : (ore1===0 ? cobblestone : iron);
			var card = <div className={"card"} id={"card_"+(i-1)+'_'+resource} onClick={(e)=> {
				var cards = this.state.cards;
				var id = e.currentTarget.id.split('_');
				cards[id[1]] = <div className={"card"}/>;
				this.setState({cards: cards});
			}} style={{ width: 50, height: 50}}><div className={"card_content"}> <img src={resource} style={{height: "80%"}}/></div></div>;
			cards.push(card);
		}
		this.setState({ cards });
	}

	render() {
		return (
			<View activePanel={this.state.activePanel} popout={this.state.popout}>
				<Panel id="main">
					<PanelHeader>Шахта</PanelHeader>
					<Div style={{display: "flex", alignItems: "center", justifyContent: "center"}}>
						<div style={{ display: "flex", alignItems: "center", justifyContent: "center" }}>
							<div className={"grid"}>
								{this.state.cards}
							</div>
						</div>
					</Div>
					<Div>
						<Footer>
							До перезагрузки: {this.state.end}
						</Footer>
					</Div>
				</Panel>
			</View>
		);
	}
}

export default App;
