import React, { PureComponent } from 'react';
import '@vkontakte/vkui/dist/vkui.css';
import {Avatar, Button, Cell} from "@vkontakte/vkui";
import {StickerImg} from "./StickerImg";

export class StickerButton extends PureComponent {

    render() {
        return (
            this.props.mode === 'cell' ?
                <Cell id={"sticker_" + this.props.i} onClick={(e) => {
                    var id = parseInt(e.currentTarget.id.split('_')[1]);
                    this.go("sticker_info");
                    this.setState({sticker_info: this.props.stickers[id]});
                }} before={<Avatar mode="app" size={78}><StickerImg src={this.props.src}/></Avatar>}
                      description={this.props.author} size={"l"}
                      bottomContent={<Button mode="commerce">{this.props.price + ' руб.'}</Button>}>{this.props.title}</Cell>
                :
                this.props.mode === 'img' &&
                <Avatar size={96}>
                    <img width={96} height={96}
                         src={this.props.src}
                         id={"user_sticker_" + this.props.i}
                         onClick={(e) => {
                             var id = parseInt(e.currentTarget.id.split('_')[2]);
                             this.setState({activeStory: "tab_main", sticker_info: this.state.stickers[id]});
                             setTimeout(() => {
                                 this.go("sticker_info");
                             }, 50)
                         }}
                    />
                </Avatar>
        )
    }

}
