import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import './css/App.css'
import eruda from 'eruda';
import html2canvas from 'html2canvas';

import { Div, Panel, Title, View, FixedLayout, Button, ScreenSpinner, platform } from '@vkontakte/vkui';

import Icon28ShareOutline from '@vkontakte/icons/dist/28/share_outline';

const blueBackground = {
	backgroundColor: 'var(--accent)'
}, redBackground = {
	backgroundColor: 'var(--dynamic_red)'
}, os = platform();

class App extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			popout: null,
			history: ['main'],
			activePanel: 'main',
			time: '',
			screen: false
		};

		this.componentDidMount = this.componentDidMount.bind(this);
		this.initializeApp = this.initializeApp.bind(this);
		this.back = this.back.bind(this);
		this.go = this.go.bind(this);
		this.change_hash = this.change_hash.bind(this);
		this.generateRandomTime = this.generateRandomTime.bind(this);
		this.updateTime = this.updateTime.bind(this);
		//eruda.init();
	}

	async componentDidMount () {
		await this.generateRandomTime();

		this.updateTime();

		window.addEventListener('popstate', e => {
			e.preventDefault();
			this.back(e);
		});

		bridge.subscribe(async ({ detail: { type, data }}) => {
			if(type !== undefined) console.log(type, data);
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = data.scheme ? data.scheme : 'client_light';
				document.body.attributes.setNamedItem(schemeAttribute);
			}else if (type === 'VKWebAppViewRestore') {
				this.setState({ popout: null, screen: false });
			}
		});
		this.initializeApp();
	}

	initializeApp() {
		let hash = window.location.hash;
		if (hash) {
			hash = hash.replace('#','');
			this.go(hash);
		}else{
			this.change_hash('main');
		}
	}

	generateRandomTime(){
		let timeDate = new Date(Date.now() + this.random(1 * 60 * 60 * 1000, 100 * 365 * 24 * 60 * 60 * 1000));
		this.setState({ timeDate });
		return true;
	}
	updateTime() {
		setInterval(()=>{
			try{
				let timeMs = this.state.timeDate.getTime() - Date.now();
				console.log(this.state.timeDate.getTime());

				let years, days, hours, minutes, seconds;
				let yMs = 1000 * 60 * 60 * 24 * 365,
					dMs = yMs / 365,
					hMs = dMs / 24,
					minMs = hMs / 60,
					sMs = minMs / 60;

				years = Math.floor(timeMs / yMs); timeMs -= yMs;
				days = Math.floor(timeMs / dMs) % 365; timeMs -= dMs;
				hours = Math.floor(timeMs / hMs) % 24; timeMs -= hMs;
				minutes = Math.floor(timeMs / minMs) % 60; timeMs -= minMs;
				seconds = Math.floor(timeMs / sMs) % 60; timeMs -= sMs;

				let times = [ [ years, 'лет' ],
						[ days, 'дней' ],
						[ hours, 'часов' ],
						[ minutes, 'минут' ],
						[ seconds, 'секунд' ]],

					timesText = [];

				for(let i = 0; i < times.length; i++){
					if (times[i][0] > 0) timesText.push(times[i][0] + ' ' + times[i][1]);
				}

				this.setState({ time: timesText.join(', ') });
			}catch (e) {
				console.error(e);
			}
		}, 500);
	}

	back = () => {
		if(this.state.popout !== null){
			this.setState({ popout: null });
			return ;
		}
		let { activePanel, history } = this.state;
		if(activePanel === 'main'){
			bridge.send('VKWebAppClose', {status: 'success'});
		}else {
			if(history.length === 1) {
				bridge.send('VKWebAppClose', {status: 'success'});
			} else if (history.length > 1) {
				history.pop();
				activePanel = history[history.length - 1];
				this.setState({activePanel});
				this.change_hash(activePanel);
			}
		}
	};
	go(panel) {
		let history = this.state.history;
		if(history[history.length-1] !== panel) {
			history.push(panel);
			window.history.pushState({activePanel: panel}, 'Title');
			this.setState({activePanel: panel, history, snackbar: null});
		}
		this.change_hash(panel);
	}
	change_hash (panel){
		bridge.send("VKWebAppSetLocation", {"location": panel});
	}

	sleep(ms) {
		return new Promise(resolve => setTimeout(resolve, ms));
	}
	random(min, max) {
		return Math.floor(Math.random() * (max - min) + min);
	}

	render() {
		return (
			<View activePanel={this.state.activePanel} popout={this.state.popout} history={this.state.history} onSwipeBack={this.back}>
				<Panel id='main' style={{ height: '100vh', width: '100vw' }}>
					<div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: '80vw', textAlign: 'center', paddingBottom: '' }}>
						<Title level='1' weight='semibold'>Мне осталось <span style={{ backgroundImage: 'linear-gradient(to top, #ff0844 0%, #ffb199 100%)', color: 'white', lineHeight: '18px', padding: '0 4px 6px 4px', display: 'inline-block' }}>жить</span></Title>
						<br/>
						<Title level='2' weight='semibold'>{this.state.time}</Title>
						<Button size='xl' style={{ marginTop: '12px', display: this.state.screen && 'none' }} onClick={()=>{ this.generateRandomTime(); }}>Паралельная вселенная</Button>
					</div>
					<FixedLayout vertical='bottom' style={{ marginBottom: '12px', display: this.state.screen && 'none' }}>
						<Div>
							<Button onClick={async ()=>{
								this.setState({ popout: <ScreenSpinner/>, screen: true });
								await this.sleep(500);
								html2canvas(document.getElementById('main')).then(canvas => {
									this.setState({ popout: null, screen: false });
									let blob = canvas.toDataURL('image/png');
									console.log(blob);
									bridge.send('VKWebAppShowStoryBox', { background_type: 'image', blob });
								});
							}} before={<Icon28ShareOutline/>} size='xl' mode='commerce'>Поделиться в истории</Button>
						</Div>
					</FixedLayout>
				</Panel>
			</View>
		);
	}
}

export default App;
