import React from 'react';
import PropTypes from 'prop-types';
import Panel from '@vkontakte/vkui/dist/components/Panel/Panel';
import PanelHeader from '@vkontakte/vkui/dist/components/PanelHeader/PanelHeader';
import Placeholder from '@vkontakte/vkui/dist/components/Placeholder/Placeholder';
import Button from '@vkontakte/vkui/dist/components/Button/Button';
import Div from '@vkontakte/vkui/dist/components/Div/Div';
import View from '@vkontakte/vkui/dist/components/View/View';
import Avatar from '@vkontakte/vkui/dist/components/Avatar/Avatar';
import Snackbar from '@vkontakte/vkui/dist/components/Snackbar/Snackbar';
import ScreenSpinner from '@vkontakte/vkui/dist/components/ScreenSpinner/ScreenSpinner';
import FormLayout from '@vkontakte/vkui/dist/components/FormLayout/FormLayout';
import Input from '@vkontakte/vkui/dist/components/Input/Input';
import Group from '@vkontakte/vkui/dist/components/Group/Group';
import List from '@vkontakte/vkui/dist/components/List/List';
import Cell from '@vkontakte/vkui/dist/components/Cell/Cell';
import CellButton from '@vkontakte/vkui/dist/components/CellButton/CellButton';

import axios from 'axios';
import connect from '@vkontakte/vk-connect';

import Icon56InfoOutline from '@vkontakte/icons/dist/56/info_outline';
import Icon16Done from '@vkontakte/icons/dist/16/done';
import Icon16Cancel from '@vkontakte/icons/dist/16/cancel';
import Icon24User from '@vkontakte/icons/dist/24/user';
import Icon24Reply from '@vkontakte/icons/dist/24/reply';

const blueBackground = {
  backgroundColor: 'var(--accent)'
};

const redBackground = {
  backgroundColor: 'var(--dynamic_red)'
};

class Home extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			activePanel: 'main_menu',
			new_url: '',
			url: 'https://oauth.vk.com/authorize?client_id=2685278&redirect_uri=https://oauth.vk.com/blank.html&display=page&scope=groups,messages,friends,offline&response_type=token&v=5.64&revoke=1',
			popout: null,
			snackbar: null,
			user_id: 0,
			auth_letter: 'https://vk.com/@vkbts_adm-auth-letter',
		};

		this.openBase = this.openBase.bind(this);
		this.onChange = this.onChange.bind(this);
		this.send_url = this.send_url.bind(this);
		this.login = this.login.bind(this);
		this.logout = this.logout.bind(this);

		connect.send("VKWebAppInit", {});
	}

	componentDidMount () {
		this.setState({ popout: <ScreenSpinner /> });
		setTimeout(() => { this.login() }, 1000);
	}

	login() {
		connect.sendPromise("VKWebAppGetUserInfo", {})
			.then(data => {
				console.log(data);
				this.setState({ user_id: data.id });
				axios({
					method: 'get',
					url: "https://admin-chat-bot.herokuapp.com/api",
					params: {
						method: 'auth',
						user_id: data.id
					},
					headers: {'Content-Type': 'application/json'},
					responseType: 'json'
				}).then(function (response) {
					var error = response.data.error;
					if(error){
						this.openBase(response.data.data, <Icon16Cancel fill="#fff" width={14} height={14} />, redBackground);
					}else{
						this.setState({ activePanel: 'main' });
						this.openBase(response.data.data, <Icon16Done fill="#fff" width={14} height={14} />, blueBackground);
					}
				}.bind(this));
			})
			.catch(error => {
				console.log(error);
			});
		this.setState({ popout: null });
	}

	onChange(e) {
		const { name, value } = e.currentTarget;
		this.setState({ [name]: value });
	}

	openBase (text, image, style) {
		this.setState({ snackbar:
		<Snackbar
			layout="vertical"
			onClose={() => this.setState({ snackbar: null })}
			before={<Avatar size={24} style={style}>{image}</Avatar>}
		>
		{text}
		</Snackbar>
		});
	}

	logout() {
		axios({
				method: 'get',
				url: "https://admin-chat-bot.herokuapp.com/api",
				params: {
					method: 'logout',
					user_id: this.state.user_id
				},
				headers: {'Content-Type': 'application/json'},
				responseType: 'json'
			}).then(function (response) {
				var error = response.data.error;
				if(error){
					this.openBase(response.data.data, <Icon16Cancel fill="#fff" width={14} height={14} />, redBackground);
				}else{
					this.setState({ activePanel: 'main_menu' });
					this.openBase(response.data.data, <Icon16Done fill="#fff" width={14} height={14} />, blueBackground);
				}
			}.bind(this));
	}

	send_url () {
		if( ( this.state.new_url.includes('http') || this.state.new_url.includes('https') ) && this.state.new_url.includes('access_token') ){
			axios({
				method: 'get',
				url: "https://admin-chat-bot.herokuapp.com/api",
				params: {
					method: 'auth',
					url: this.state.new_url
				},
				headers: {'Content-Type': 'application/json'},
				responseType: 'json'
			}).then(function (response) {
				var error = response.data.error;
				if(error){
					this.openBase(response.data.data, <Icon16Cancel fill="#fff" width={14} height={14} />, redBackground);
				}else{
					this.setState({ activePanel: 'main', new_url: '' });
					this.openBase(response.data.data, <Icon16Done fill="#fff" width={14} height={14} />, blueBackground);
				}
			}.bind(this));
		}else{
			this.openBase('Некорректная ссылка', <Icon16Cancel fill="#fff" width={14} height={14} />, redBackground);
		}
	} 

	render() {
		const { new_url } = this.state.new_url;
		return (
		<View popout={this.state.popout} activePanel={this.state.activePanel}>
			<Panel id="main_menu">
				<PanelHeader>VK App</PanelHeader>

				<Placeholder
					icon={<Icon56InfoOutline />}
					title="Авторизация"
					action={<Button size="xl" onClick={ () => this.setState({ activePanel: 'auth' })} level="tertiary">Войти</Button>}
					stretched
				>
					Для получения доступа ко всем функциям, необходимо авторизоваться
				</Placeholder>
			</Panel>

			<Panel id="auth" theme="white" >
				<PanelHeader> Авторизация </PanelHeader>
				<FormLayout>
					<Div>
					Чтобы авторизоваться, перейдите по <a target='_blank' href={this.state.url}>ссылке</a>. Далее авторизуйтесь через ВКонтакте.
					У Вас изменится ссылка в адресной строке, вставьте её в поле ниже.</Div>

					<Input
						top = "Новая ссылка"
						placeHolder ="Пример: https://oauth.vk.com/blank.html#access_token=abcdefg&expires_in=0&user_id=123"
						name="new_url"
						value={new_url}
						onChange={this.onChange}
					/>

					<Button size="xl" onClick={this.send_url}> Продолжить </Button>

					<Button level="tertiary"><a target='_blank' href={this.state.auth_letter}> Зачем это нужно? </a></Button>

				</FormLayout>
			</Panel>

			<Panel id="main">
				<PanelHeader> Главная </PanelHeader>
				<Group>
					<List>
						<CellButton level="danger" onClick={this.logout} before={<Icon24Reply />}>Выйти</CellButton>
					</List>
				</Group>
			</Panel>
		{this.state.snackbar}
		</View>
		);
	}
}

Home.propTypes = {
	id: PropTypes.string.isRequired,
	go: PropTypes.func.isRequired,
};

export default Home;
