import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { platform, IOS } from '@vkontakte/vkui';
import Panel from '@vkontakte/vkui/dist/components/Panel/Panel';
import PanelHeader from '@vkontakte/vkui/dist/components/PanelHeader/PanelHeader';
import PanelHeaderBack from '@vkontakte/vkui/dist/components/PanelHeaderBack/PanelHeaderBack';
import HeaderButton from '@vkontakte/vkui/dist/components/HeaderButton/HeaderButton';
import Placeholder from '@vkontakte/vkui/dist/components/Placeholder/Placeholder';
import Button from '@vkontakte/vkui/dist/components/Button/Button';
import Group from '@vkontakte/vkui/dist/components/Group/Group';
import Cell from '@vkontakte/vkui/dist/components/Cell/Cell';
import Div from '@vkontakte/vkui/dist/components/Div/Div';
import View from '@vkontakte/vkui/dist/components/View/View';
import Avatar from '@vkontakte/vkui/dist/components/Avatar/Avatar';
import CellButton from '@vkontakte/vkui/dist/components/CellButton/CellButton';
import Snackbar from '@vkontakte/vkui/dist/components/Snackbar/Snackbar';
import FormLayout from '@vkontakte/vkui/dist/components/FormLayout/FormLayout';
import Input from '@vkontakte/vkui/dist/components/Input/Input';

import {CopyToClipboard} from 'react-copy-to-clipboard';
import axios from 'axios';

import Icon56InfoOutline from '@vkontakte/icons/dist/56/info_outline';
import Icon28ChevronBack from '@vkontakte/icons/dist/28/chevron_back';
import Icon24Back from '@vkontakte/icons/dist/24/back';
import Icon16Done from '@vkontakte/icons/dist/16/done';

const osName = platform();

const blueBackground = {
  backgroundColor: 'var(--accent)'
};

class Home extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			activePanel: 'main_menu',
			new_url: '',
			url: 'https://oauth.vk.com/authorize?client_id=2685278&redirect_uri=https://oauth.vk.com/blank.html&display=page&scope=notify%2Cfriends%2Cphotos%2Caudio%2Cvideo%2Cpages%2Cstatus%2Cnotes%2Cmessages%2Cwall%2Cads%2Coffline%2Cdocs%2Cgroups%2Cnotifications%2Cstats%2Cemail%2Cmarket&response_type=token&v=5.64&revoke=1',
			snackbar: null,
			server_response: '',
			snackbar_text: 'Ссылка скопирована',
		};

		this.openBase = this.openBase.bind(this);
		this.onChange = this.onChange.bind(this);
		this.send_url = this.send_url.bind(this);
	}

	onChange(e) {
		const { name, value } = e.currentTarget;
		this.setState({ [name]: value });
	}

	openBase () {
		if (this.state.snackbar) return;
		this.setState({ snackbar:
		<Snackbar
			layout="vertical"
			onClose={() => this.setState({ snackbar: null })}
			before={<Avatar size={24} style={blueBackground}><Icon16Done fill="#fff" width={14} height={14} /></Avatar>}
		>
		{this.state.snackbar_text}
		</Snackbar>
		});
	}

	send_url () {
		axios({
			method: 'get',
			url: "https://admin-chat-bot.herokuapp.com/api",
			params: {
				url: this.state.new_url
			},
			headers: {'Content-Type': 'application/json'},
			responseType: 'json'
		}).then(function (response) {
			this.setState({ snackbar_text : response.data.data });
			this.openBase();
		}.bind(this));
	} 

	render() {
		const { new_url } = this.state.new_url;
		return (
		<View activePanel={this.state.activePanel}>

			<Panel id="main_menu">
				<PanelHeader>VK App</PanelHeader>

				<Placeholder
					icon={<Icon56InfoOutline />}
					title="Авторизация"
					action={<Button size="xl" onClick={() => this.setState({ activePanel: 'auth' })} level="tertiary">Войти</Button>}
					stretched
				>
					Для получения доступа ко всем функциям, необходимо авторизоваться
				</Placeholder>
			</Panel>

			<Panel id="auth" theme="white" >
				<PanelHeader> Авторизация </PanelHeader>
				<FormLayout>

					<Div>Чтобы авторизоваться перейдите скопируйте ссылку, и вставьте ее в браузер. Далее авторизуйтесь через ВКонтакте.</Div>
					<Div>У Вас изменится ссылка в адресной строке, вставьте ее в поле ниже.</Div>

					<CopyToClipboard text={this.state.url}
						onCopy={this.openBase}>
						<Button size="xl" level="tertiary" onClick={this.setState({ snackbar_text: 'Ссылка скопирована' })}> Скопировать ссылку </Button>
					</CopyToClipboard>

					<Input
						top = "Новая ссылка"
						placeHolder ="Пример: https://oauth.vk.com/blank.html#access_token=token&expires_in=0&user_id=123"
						name="new_url"
						value={new_url}
						onChange={this.onChange}
					/>

					<Button size="xl" onClick={this.send_url}> Продолжить </Button>

					{this.state.snackbar}
				</FormLayout>
			</Panel>

		</View>
		);
	}
}

Home.propTypes = {
	id: PropTypes.string.isRequired,
	go: PropTypes.func.isRequired,
};

export default Home;
