import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import './css/App.css'
import eruda from 'eruda';
import html2canvas from 'html2canvas';

import { Div, Panel, Title, Headline, View, FixedLayout, Button, ScreenSpinner, platform } from '@vkontakte/vkui';

import Icon28StoryOutline from '@vkontakte/icons/dist/28/story_outline';
import Icon24ImageFilterOutline from '@vkontakte/icons/dist/24/image_filter_outline';

const blueBackground = {
	backgroundColor: 'var(--accent)'
}, redBackground = {
	backgroundColor: 'var(--dynamic_red)'
}, os = platform();

class App extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			popout: null,
			history: ['main'],
			activePanel: 'main',
			time: '',
			screen: false,
			scheme: 'bright_light',
			paralel: false,
		};

		this.componentDidMount = this.componentDidMount.bind(this);
		this.initializeApp = this.initializeApp.bind(this);
		this.back = this.back.bind(this);
		this.go = this.go.bind(this);
		this.change_hash = this.change_hash.bind(this);
		this.generateRandomTime = this.generateRandomTime.bind(this);
		this.updateTime = this.updateTime.bind(this);
		//eruda.init();
	}

	async componentDidMount () {
		let generated = await this.generateRandomTime(false);

		this.updateTime();

		window.addEventListener('popstate', e => {
			e.preventDefault();
			this.back(e);
		});

		bridge.subscribe(async ({ detail: { type, data }}) => {
			if(type !== undefined) console.log(type, data);
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = false ? data.scheme ? data.scheme : 'bright_light' : 'space_gray';
				document.body.attributes.setNamedItem(schemeAttribute);
				this.setState({ scheme: schemeAttribute.value });
				console.log(this.state.scheme);
			}else if (type === 'VKWebAppViewRestore') {
				this.setState({ popout: null, screen: false });
				let shapes = document.getElementsByClassName('bg_shape_container');
				for(let element of shapes){
					element.delete();
				}
			}
		});
		this.initializeApp();
	}

	initializeApp() {
		bridge.send('VKWebAppInit');

		let hash = window.location.hash;
		if (hash) {
			hash = hash.replace('#','');
			this.go(hash);
		}else{
			this.change_hash('main');
		}

		let imgs = ['https://image.flaticon.com/icons/svg/2893/2893113.svg', 'https://image.flaticon.com/icons/svg/2893/2893195.svg'];

		let bgInterval = setInterval(()=>{
			let shape_container = document.createElement('div');
			shape_container.className = 'bg_shape_container';
			let shape = document.createElement('img');
			shape.className = 'bg_shape light_outline';
			shape.src = imgs[this.random(0, imgs.length)];
			shape.style.left = (this.random(-15, 90)) + 'vw';
			shape_container.appendChild(shape);
			document.getElementById('bg_shapes').appendChild(shape_container);
			for(let i = 0; i < 5; i++){
				shape.style.setProperty('--offset-x-' + i, this.random(-20, 20) +'vw');
			}
			setTimeout(()=>{
				shape_container.remove();
			}, 10000);
		}, 600);
	}

	async generateRandomTime(isParalel){
		let timeDate = await new Date(Date.now() + this.random(1 * 60 * 60 * 1000, 100 * 365 * 24 * 60 * 60 * 1000));
		let reasons = [ 'голода', 'жажды', 'диеты', 'нехватки кислорода' ];
		if(!this.state.reason2){
			if(isParalel) {
				await this.setState({ reason2: reasons[this.random(0, reasons.length)] });
				await this.setState({timeDate2: timeDate});
			}else {
				await this.setState({ reason: reasons[this.random(0, reasons.length)] });
				await this.setState({timeDate});
			}
		}
		await this.setState({paralel: isParalel});
		return true;
	}
	updateTime() {
		setInterval(()=>{
			try{
				let timeMs = (this.state.paralel ? this.state.timeDate2.getTime() : this.state.timeDate.getTime()) - Date.now();

				let years, days, hours, minutes, seconds;
				let yMs = 1000 * 60 * 60 * 24 * 365,
					dMs = yMs / 365,
					hMs = dMs / 24,
					minMs = hMs / 60,
					sMs = minMs / 60;

				years = Math.floor(timeMs / yMs); timeMs -= yMs;
				days = Math.floor(timeMs / dMs) % 365; timeMs -= dMs;
				hours = Math.floor(timeMs / hMs) % 24; timeMs -= hMs;
				minutes = Math.floor(timeMs / minMs) % 60; timeMs -= minMs;
				seconds = Math.floor(timeMs / sMs) % 60; timeMs -= sMs;

				let times = [ [ years, 'лет' ],
						[ days, 'дней' ],
						[ hours, 'ч.' ],
						[ minutes, 'мин.' ],
						[ seconds, 'сек.' ]],

					timesText = [];

				for(let i = 0; i < times.length; i++){
					if (times[i][0] > 0) timesText.push(times[i][0] + ' ' + times[i][1]);
				}

				this.setState({ time: timesText.join(', ') });
			}catch (e) {
				console.error(e);
			}
		}, 500);
	}

	back = () => {
		if(this.state.popout !== null){
			this.setState({ popout: null });
			return ;
		}
		let { activePanel, history } = this.state;
		if(activePanel === 'main'){
			bridge.send('VKWebAppClose', {status: 'success'});
		}else {
			if(history.length === 1) {
				bridge.send('VKWebAppClose', {status: 'success'});
			} else if (history.length > 1) {
				history.pop();
				activePanel = history[history.length - 1];
				this.setState({activePanel});
				this.change_hash(activePanel);
			}
		}
	};
	go(panel) {
		let history = this.state.history;
		if(history[history.length-1] !== panel) {
			history.push(panel);
			window.history.pushState({activePanel: panel}, 'Title');
			this.setState({activePanel: panel, history, snackbar: null});
		}
		this.change_hash(panel);
	}
	change_hash (panel){
		bridge.send("VKWebAppSetLocation", {"location": panel});
	}

	sleep(ms) {
		return new Promise(resolve => setTimeout(resolve, ms));
	}
	random(min, max) {
		return Math.floor(Math.random() * (max - min) + min);
	}

	render() {
		return (
			<View activePanel={this.state.activePanel} popout={this.state.popout} history={this.state.history} onSwipeBack={this.back}>
				<Panel id='main' style={{ height: '100vh', width: '100vw' }}>
					<div id='bg_shapes'/>
					<div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: '80vw', textAlign: 'center', paddingBottom: '' }}>
						<Title level='1' weight='semibold'>Мне осталось <span style={{ backgroundImage: 'linear-gradient(to left, #fc6076 0%, #ff9a44 100%)', color: 'white', lineHeight: '18px', padding: '0 4px 8px 4px', display: 'inline-block' }}>жить</span></Title>
						<Title level='2' weight='semibold' style={{ marginTop: '12px' }}>{this.state.time}</Title>
						<Headline weight='semibold' style={{ marginTop: '12px', color: 'white', background: 'linear-gradient(to right, #ed6ea0 0%, #ec8c69 100%)', padding: '0 4px 4px 4px', display: 'inline-block' }}>{ 'Я умру из-за ' + ( this.state.paralel ? this.state.reason2: this.state.reason ) }</Headline><br/>
						<Button before={<Icon24ImageFilterOutline width={20} height={20} style={{ paddingRight: '4px' }}/>} size='l' style={{ marginTop: '12px', display: this.state.screen && 'none' }} onClick={async ()=>{ await this.generateRandomTime(!this.state.paralel); }}>{ this.state.paralel ? 'Вернуться' : 'Паралельная вселенная' }</Button>
					</div>

					<FixedLayout vertical='bottom' style={{ marginBottom: '12px', display: this.state.screen && 'none' }}>
						<Div>
							<Button onClick={async ()=>{
								this.setState({ popout: <ScreenSpinner/>, screen: true });
								await this.sleep(250);
								html2canvas(document.getElementsByClassName('View__panels')[0]).then(async canvas => {
									let blob = canvas.toDataURL('image/png');
									try{
										await bridge.send('VKWebAppShowStoryBox', { background_type: 'image', blob });
									}catch (e) {}
									this.setState({ popout: null, screen: false });
								});
							}} before={<Icon28StoryOutline/>} size='xl' mode='commerce'>Поделиться в истории</Button>
						</Div>
					</FixedLayout>
				</Panel>
			</View>
		);
	}
}

export default App;
